package com.example.myproject;

import android.os.Bundle;
import android.view.View;
import android.widget.Button;
import androidx.appcompat.app.AppCompatActivity;

public class StepperActivity extends AppCompatActivity {
    private StepperAdapter stepperAdapter;
    private Button btnNext, btnPrevious;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_stepper1);

        // Initialize stepper
        View stepperView = findViewById(R.id.stepperRoot);
        stepperAdapter = new StepperAdapter(stepperView);

        // Initialize buttons
        btnNext = findViewById(R.id.btnNext);
        btnPrevious = findViewById(R.id.btnPrevious);

        btnNext.setOnClickListener(v -> {
            stepperAdapter.nextStep();
            updateButtonStates();
        });

        btnPrevious.setOnClickListener(v -> {
            stepperAdapter.previousStep();
            updateButtonStates();
        });

        // Initial button states
        updateButtonStates();
    }

    private void updateButtonStates() {
        btnPrevious.setEnabled(stepperAdapter.getCurrentStep() > 1);
        btnNext.setEnabled(stepperAdapter.getCurrentStep() < 4);
    }
}

package com.example.myproject;

import android.view.View;
import android.widget.FrameLayout;
import android.widget.TextView;

public class StepperAdapter {
    private View stepperView;
    private int currentStep = 1;
    private final int totalSteps = 4;

    public StepperAdapter(View stepperView) {
        this.stepperView = stepperView;
        updateStepperUI();
    }

    public void nextStep() {
        if (currentStep < totalSteps) {
            currentStep++;
            updateStepperUI();
        }
    }

    public void previousStep() {
        if (currentStep > 1) {
            currentStep--;
            updateStepperUI();
        }
    }

    public void goToStep(int step) {
        if (step >= 1 && step <= totalSteps) {
            currentStep = step;
            updateStepperUI();
        }
    }

    public int getCurrentStep() {
        return currentStep;
    }

    private void updateStepperUI() {
        for (int i = 1; i <= totalSteps; i++) {
            // Update dots
            FrameLayout dot = stepperView.findViewById(
                    stepperView.getResources().getIdentifier("dot" + i, "id", stepperView.getContext().getPackageName())
            );
            dot.setBackgroundResource(i <= currentStep ? 
                    R.drawable.step_circle_active : R.drawable.step_circle_inactive);

            // Update lines (except for the last step)
            if (i < totalSteps) {
                View line = stepperView.findViewById(
                        stepperView.getResources().getIdentifier("line" + i, "id", stepperView.getContext().getPackageName())
                );
                line.setBackgroundResource(i < currentStep ? 
                        R.drawable.step_line_active : R.drawable.step_line_inactive);
            }

            // Update text color
            TextView label = stepperView.findViewById(
                    stepperView.getResources().getIdentifier("label" + i, "id", stepperView.getContext().getPackageName())
            );
            label.setAlpha(i <= currentStep ? 1.0f : 0.5f);
        }
    }
}
